import React from "react";
import "./Card.css";

// Import SVGs as components
import { ReactComponent as LoaderIcon } from "../icons_FEtask/loader.svg";
import { ReactComponent as CircleIcon } from "../icons_FEtask/circle.svg";
import { ReactComponent as AdjustIcon } from "../icons_FEtask/adjust.svg";
import { ReactComponent as CheckCircleIcon } from "../icons_FEtask/check-circle.svg";
import { ReactComponent as CloseCircleIcon } from "../icons_FEtask/close-circle.svg";
import { ReactComponent as ExclamationSquareIcon } from "../icons_FEtask/exclamation-square.svg";
import { ReactComponent as SignalIcon } from "../icons_FEtask/signal.svg";

const Card = ({ id, title, tag, status, priority }) => {
  const isStatus = localStorage.getItem("group") === "status";
  const isPriority = localStorage.getItem("group") === "priority";
  const statusOrder = ["Backlog", "Todo", "In progress", "Done"];

  const getStatusIndex = (status) => {
    return statusOrder.indexOf(status);
  };

  return (
    <div className="cardContainer flex-gap-10" style={{ gap: "5px" }}>
      <div className="cardHeading flex-sb">
        <span style={{ textTransform: "uppercase" }} className="color-grey">
          {id}
        </span>
        <div
          className="imageContainer relative"
          style={{ width: "30px", height: "30px" }}
        >
          <img
            style={{ width: "95%", height: "95%", borderRadius: "50%" }}
            src="https://media.istockphoto.com/id/1300845620/vector/user-icon-flat-isolated-on-white-background-user-symbol-vector-illustration.jpg?s=612x612&w=0&k=20&c=yBeyba0hUkh14_jgv1OKqIH0CCSWU_4ckRkAoy2p73o="
            alt="UserImage"
          />
          <div className="showStatus"></div>
        </div>
      </div>
      <div className="cardTitle" style={{ fontWeight: 200 }}>
        {!isStatus && (
          <>
            {status === "Backlog" ? (
              <LoaderIcon style={{ fontSize: "14px" }} />
            ) : status === "Todo" ? (
              <CircleIcon style={{ fontSize: "13px", color: "#ddeded" }} />
            ) : status === "In progress" ? (
              <AdjustIcon style={{ fontSize: "14px", color: "#f2d750" }} />
            ) : status === "Done" ? (
              <CheckCircleIcon />
            ) : (
              <CloseCircleIcon />
            )}
          </>
        )}
        <span style={{ margin: "0.2px" }}>{title}</span>
      </div>
      <div className="cardTags">
        {!isPriority ? (
          <div className="tags color-grey">
            {priority === 1 || priority === 2 || priority === 3 ? (
              <SignalIcon
                style={{
                  width: "30px",
                  height: "30px",
                  opacity: priority === 3 ? 1 : 0.25,
                }}
              />
            ) : priority === 4 ? (
              <ExclamationSquareIcon />
            ) : (
              <p>...</p>
            )}
          </div>
        ) : null}
        {tag?.map((element, index) => (
          <div key={index} className="tags color-grey">
            <span>•</span> {element}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Card;
